mapper.update:
update sws_request_log set
secret = #{secret,jdbcType=VARCHAR},
salt = #{salt,jdbcType=VARCHAR}, token = #{token,jdbcType=VARCHAR}, update_time = now() where app_id = #{appId,jdbcType=VARCHAR} and is_del = 1;

mapper.select:
select
count(1) count
from sws_request_log
where app_id = #{appId}
and secret = #{secret}
and is_del = 1;

select
count(1) count
from sws_request_log
where app_id = #{appId}
and
is_del = 1;

select
count(1) count
from sws_external_user
where app_id = #{appId}
and secret = #{secret}
and
is_del = 1;
